class e extends HTMLElement{constructor(){super(),this.links=[{name:"Home",href:"",active:this.checkPathname("")},{name:"Films",href:"pages/films/",active:this.checkPathname("films")},{name:"Collections",href:"pages/collections/",active:this.checkPathname("collections")}]}checkPathname(e){const n=window.location.pathname.split("/");return n[n.length-2]===e}hrefJoin(e){const n="Web-Development-Basics/";return window.location.href.slice(0,window.location.href.search(n)+n.length)+e}connectedCallback(){this.innerHTML=`\n      <header class="header">\n        <img class="header__logo" src="/CodersCamp2021.Project.Web-Development-Basics/assets/logo.31c828cb.svg" height="60">\n        <nav class="header__nav">\n          <ul class="header__nav-list">\n            ${this.links.map((e=>`<li class="header__nav-item ${e.active?"header__nav-item--active":""}">\n                    <a class="header__nav-link" href="${this.hrefJoin(e.href)}">${e.name}\n                  </li>`)).join("")}\n          </ul>\n        </nav>\n      </header>\n    `}}customElements.define("header-component",e);class n extends HTMLElement{connectedCallback(){this.innerHTML='\n        <footer class="footer">\n          <div class="footer__nav-link">\n              <a href="https://www.coderscamp.edu.pl/" target="_blank">\n                <img class="footer__logo" src="/CodersCamp2021.Project.Web-Development-Basics/assets/CodersCamp.731b2d0a.png" alt="coderscamp-logo">\n              </a>\n              <a href="https://github.com/mcwiekala/CodersCamp2021.Project.Web-Development-Basics" target="_blank">\n                <img class="footer__logo" src="/CodersCamp2021.Project.Web-Development-Basics/assets/GitHub.1a0dfbed.png" alt="github-logo">\n               </a>\n          </div>\n        </footer>\n      '}}customElements.define("footer-component",n);
